const raw = $input.first().json.output || "";  // หรือใช้ชื่อ field ที่ได้จาก AI

// ทำความสะอาดข้อความสำหรับ gTTS
const cleaned = raw
  // แทนที่ขึ้นบรรทัดใหม่หลายบรรทัดด้วยช่องว่าง
  .replace(/\n{2,}/g, ' ')
  // แทนที่ขึ้นบรรทัดใหม่เดี่ยวด้วยช่องว่าง
  .replace(/\n/g, ' ')
  // ลบเครื่องหมายคำพูดคู่ออก
  .replace(/"/g, '')
  // ลบเครื่องหมายคำพูดเดี่ยวออก (ถ้ามี)
  .replace(/'/g, '')
  // ลบ backticks ออก (ถ้ามี)
  .replace(/`/g, '')
  // ลบอักขระพิเศษที่อาจทำให้ gTTS อ่านผิด
  .replace(/[\*\#\[\]]/g, '')
  // แทนที่ช่องว่างหลายช่องด้วยช่องว่างเดียว
  .replace(/\s+/g, ' ')
  // ลบช่องว่างที่ขึ้นต้นและจบ
  .trim();

// ตรวจสอบความยาวข้อความ (gTTS มีขีดจำกัด)
const maxLength = 5000; // ปรับตามต้องการ
const finalText = cleaned.length > maxLength 
  ? cleaned.substring(0, maxLength) + '...' 
  : cleaned;

// ตรวจสอบว่าข้อความว่างหรือไม่
const safeText = finalText || "ขออภัย ไม่สามารถประมวลผลคำตอบได้";

return [
  {
    json: {
      reply: safeText
    }
  }
];
