// ===== วิธีที่ 3: หา div แบบ nested-safe =====
function findDivAfterH2(html) {
  // หา position ของ h2
  const h2Pattern = /<h2[^>]*>Top Heroes<\/h2>/;
  const h2Match = html.match(h2Pattern);

  if (!h2Match) return null;

  // เริ่มหาจากตำแหน่งหลัง h2
  let startIndex = h2Match.index + h2Match[0].length;

  // ข้าม whitespace
  while (startIndex < html.length && /\s/.test(html[startIndex])) {
    startIndex++;
  }

  // ตรวจสอบว่าตำแหน่งถัดไปเป็น <div หรือไม่
  if (!html.substring(startIndex).startsWith('<div')) {
    return null;
  }

  // หา closing tag ของ div ที่ match
  let divCount = 0;
  let currentIndex = startIndex;
  let divEnd = -1;

  while (currentIndex < html.length) {
    if (html.substring(currentIndex, currentIndex + 4) === '<div') {
      divCount++;
      // ข้ามไปหลัง >
      while (currentIndex < html.length && html[currentIndex] !== '>') {
        currentIndex++;
      }
      currentIndex++;
    } else if (html.substring(currentIndex, currentIndex + 6) === '</div>') {
      divCount--;
      if (divCount === 0) {
        divEnd = currentIndex + 6;
        break;
      }
      currentIndex += 6;
    } else {
      currentIndex++;
    }
  }

  if (divEnd === -1) return null;

  return html.substring(startIndex, divEnd);
}

const htmlString3 = $input.first().json.data.html;
const extractedDiv = findDivAfterH2(htmlString3);

if (extractedDiv) {
  return [{
    json: {
      div: extractedDiv,
      posion: 'Carry',
      ref: $input.first().json.data.url,
      success: true
    }
  }];
}

