return [
  {
    json: {
      finaldata: merged
    }
  }
];

// ต่อข้อความลงท้าย string สุดท้าย
const suffix = `

📊 Data Source  

MMR Range: 7000+  

Reference Links:  
- [Carry Meta](https://dota2protracker.com/meta?mmr=7000&position=pos%2B1&period=8)  
- [Midlane Meta](https://dota2protracker.com/meta?mmr=7000&position=pos%2B2&period=8)  
- [Offlane Meta](https://dota2protracker.com/meta?mmr=7000&position=pos%2B3&period=8)  
- [Support Meta](https://dota2protracker.com/meta?mmr=7000&position=pos%2B4&period=8)  
- [Hard Support Meta](https://dota2protracker.com/meta?mmr=7000&position=pos%2B5&period=8)
`;
const fullText = $input.first().json.output + suffix;

// ฟังก์ชันสำหรับตัดข้อความไม่ให้เกิน 2000 ตัวอักษร
function splitMessage(text, maxLength = 2000) {
  const parts = [];
  let current = 0;
  while (current < text.length) {
    parts.push(text.slice(current, current + maxLength));
    current += maxLength;
  }
  return parts;
}

const messages = splitMessage(fullText);

return messages.map(msg => ({
  json: {
    message: msg
  }
}));
